import React, { useState, useEffect } from 'react';
import ProductGrid from './components/ProductGrid';
import Cart from './components/Cart';
import Checkout from './components/Checkout';
import Receipt from './components/Receipt';
import './App.css';

function App() {
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [showCheckout, setShowCheckout] = useState(false);
  const [receipt, setReceipt] = useState(null);
  const [cartKey, setCartKey] = useState(0); // Add this for force refresh

  // Load cart from localStorage on startup
  useEffect(() => {
    const savedCart = localStorage.getItem('vibe-commerce-cart');
    if (savedCart) {
      setCart(JSON.parse(savedCart));
    }
  }, []);

  // Save cart to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('vibe-commerce-cart', JSON.stringify(cart));
  }, [cart]);

  const handleCheckoutSuccess = (orderReceipt) => {
    console.log('ðŸ”„ Clearing cart and forcing refresh...');

    // Clear everything
    setCart([]);
    setReceipt(orderReceipt);
    setShowCheckout(false);

    // Force refresh by changing the key
    setCartKey(prev => prev + 1);

    // Double clear localStorage
    localStorage.setItem('vibe-commerce-cart', JSON.stringify([]));

    console.log('âœ… Cart should be cleared now');
  };

  const handleCloseReceipt = () => {
    setReceipt(null);
    setShowCart(false);
  };

  // Force fetch from backend after checkout
  const refreshCartFromBackend = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/cart');
      if (response.ok) {
        const data = await response.json();
        setCart(data.items);
      }
    } catch (error) {
      console.error('Error refreshing cart:', error);
    }
  };

  // Refresh cart when receipt is shown (after checkout)
  useEffect(() => {
    if (receipt) {
      refreshCartFromBackend();
    }
  }, [receipt]);

  return (
    <div className="App">
      <header className="app-header">
        <h1>Vibe Commerce</h1>
        <button 
          className="cart-button"
          onClick={() => setShowCart(!showCart)}
        >
          Cart ({cart.reduce((total, item) => total + item.quantity, 0)})
        </button>
      </header>

      {showCheckout && (
        <Checkout 
          cart={cart}
          onCheckoutSuccess={handleCheckoutSuccess}
          onClose={() => setShowCheckout(false)}
        />
      )}

      {receipt && (
        <Receipt 
          receipt={receipt}
          onClose={handleCloseReceipt}
        />
      )}

      {showCart ? (
        <Cart 
          key={cartKey} // Force re-render with key change
          cart={cart} 
          setCart={setCart}
          setShowCheckout={setShowCheckout}
        />
      ) : (
        <div>
          <h2>Products</h2>
          <ProductGrid cart={cart} setCart={setCart} />
        </div>
      )}
    </div>
  );
}

export default App;
