import React, { useState, useEffect } from 'react';

const Cart = ({ cart, setCart, setShowCheckout }) => {
  const [isLoading, setIsLoading] = useState(false);

  // Fetch latest cart data when component mounts
  useEffect(() => {
    fetchCartFromBackend();
  }, []);

  // Function to fetch current cart from backend
  const fetchCartFromBackend = async () => {
    try {
      setIsLoading(true);
      const response = await fetch('http://localhost:5000/api/cart');
      if (response.ok) {
        const data = await response.json();
        setCart(data.items); // Sync with backend
        console.log('ðŸ”„ Cart synced from backend:', data.items);
      }
    } catch (error) {
      console.error('Error fetching cart:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const removeFromCart = async (productId) => {
    try {
      // First update backend
      const response = await fetch(`http://localhost:5000/api/cart/${productId}`, {
        method: 'DELETE'
      });

      if (!response.ok) {
        throw new Error('Failed to remove item');
      }

      // Then refresh cart from backend to ensure sync
      await fetchCartFromBackend();
      
    } catch (error) {
      console.error('Error removing from cart:', error);
      // Fallback: update frontend state only
      setCart(cart.filter(item => item.id !== productId));
    }
  };

  const updateQuantity = async (productId, newQuantity) => {
    if (newQuantity < 1) {
      removeFromCart(productId);
      return;
    }

    try {
      // For quantity updates, we need to handle differently
      // Since we don't have a PATCH endpoint, we'll remove and re-add
      if (newQuantity === 0) {
        await removeFromCart(productId);
        return;
      }

      // Find the current item
      const item = cart.find(item => item.id === productId);
      if (!item) return;

      // Calculate quantity difference
      const quantityDiff = newQuantity - item.quantity;
      
      if (quantityDiff > 0) {
        // Add more items
        const response = await fetch('http://localhost:5000/api/cart', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            productId: productId,
            quantity: quantityDiff
          })
        });

        if (!response.ok) {
          throw new Error('Failed to update quantity');
        }
      } else if (quantityDiff < 0) {
        // Remove some items - we'll handle by removing and re-adding correct quantity
        // This is a bit hacky but works without a PATCH endpoint
        await removeFromCart(productId);
        if (newQuantity > 0) {
          const response = await fetch('http://localhost:5000/api/cart', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              productId: productId,
              quantity: newQuantity
            })
          });
        }
      }

      // Refresh cart from backend
      await fetchCartFromBackend();

    } catch (error) {
      console.error('Error updating quantity:', error);
      // Fallback: update frontend state only
      setCart(cart.map(item => 
        item.id === productId ? { ...item, quantity: newQuantity } : item
      ));
    }
  };

  const getTotal = () => {
    return cart.reduce((total, item) => total + (item.price * item.quantity), 0).toFixed(2);
  };

  if (isLoading) {
    return (
      <div className="cart">
        <h2>Shopping Cart</h2>
        <div className="loading">Loading cart...</div>
      </div>
    );
  }

  return (
    <div className="cart">
      <h2>Shopping Cart</h2>
      {cart.length === 0 ? (
        <p className="empty-cart">Your cart is empty</p>
      ) : (
        <div>
          {cart.map(item => (
            <div key={item.id} className="cart-item">
              <div className="item-info">
                <h4>{item.name}</h4>
                <p className="item-price">Price: ${item.price}</p>
              </div>
              <div className="quantity-controls">
                <button onClick={() => updateQuantity(item.id, item.quantity - 1)}>-</button>
                <span className="quantity">{item.quantity}</span>
                <button onClick={() => updateQuantity(item.id, item.quantity + 1)}>+</button>
              </div>
              <button 
                onClick={() => removeFromCart(item.id)}
                className="remove-btn"
              >
                Remove
              </button>
            </div>
          ))}
          <div className="cart-total">
            <h3>Total: ${getTotal()}</h3>
          </div>
          
          {cart.length > 0 && (
            <button 
              onClick={() => setShowCheckout(true)}
              className="checkout-btn"
            >
              Proceed to Checkout
            </button>
          )}
        </div>
      )}
    </div>
  );
};

export default Cart;
