import React, { useState, useEffect } from 'react';
import ProductGrid from './components/ProductGrid';
import Cart from './components/Cart';
import Checkout from './components/Checkout';
import Receipt from './components/Receipt';
import './App.css';

function App() {
  // Load cart from localStorage on startup
  const [cart, setCart] = useState(() => {
    const savedCart = localStorage.getItem('vibe-commerce-cart');
    console.log('ðŸ”„ Loading cart from localStorage:', savedCart);
    return savedCart ? JSON.parse(savedCart) : [];
  });

  const [showCart, setShowCart] = useState(false);
  const [showCheckout, setShowCheckout] = useState(false);
  const [receipt, setReceipt] = useState(null);

  // Save cart to localStorage whenever it changes
  useEffect(() => {
    console.log('ðŸ’¾ Saving cart to localStorage:', cart);
    localStorage.setItem('vibe-commerce-cart', JSON.stringify(cart));
  }, [cart]);

  const handleCheckoutSuccess = (orderReceipt) => {
    console.log('ðŸŽ¯ Checkout successful, clearing cart...');
    console.log('Before clear - cart:', cart);

    setReceipt(orderReceipt);
    setShowCheckout(false);
    setCart([]); // Clear the cart

    console.log('After clear - cart should be empty');
  };

  const handleCloseReceipt = () => {
    console.log('ðŸ“¦ Closing receipt, cart should be empty:', cart);
    setReceipt(null);
    setShowCart(false);
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>Vibe Commerce</h1>
        <button 
          className="cart-button"
          onClick={() => setShowCart(!showCart)}
        >
          Cart ({cart.reduce((total, item) => total + item.quantity, 0)})
        </button>
      </header>

      {showCheckout && (
        <Checkout 
          cart={cart}
          onCheckoutSuccess={handleCheckoutSuccess}
          onClose={() => setShowCheckout(false)}
        />
      )}

      {receipt && (
        <Receipt 
          receipt={receipt}
          onClose={handleCloseReceipt}
        />
      )}

      {showCart ? (
        <Cart 
          cart={cart} 
          setCart={setCart}
          setShowCheckout={setShowCheckout}
        />
      ) : (
        <div>
          <h2>Products</h2>
          <ProductGrid cart={cart} setCart={setCart} />
        </div>
      )}
    </div>
  );
}

export default App;
