import React, { useState, useEffect } from 'react';
import ProductCard from './ProductCard';

const ProductGrid = ({ cart, setCart }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchProducts();
    fetchCartFromBackend(); // Sync with backend cart on page load
  }, []);

  // Fetch products from backend
  const fetchProducts = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await fetch('http://localhost:5000/api/products');

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error('Error fetching products:', error);
      setError('Failed to load products. Please try again later.');

      // Fallback to mock data if API is not available
      const mockProducts = [
        { 
          id: 1, 
          name: "Wireless Headphones", 
          price: 99.99, 
          description: "High-quality wireless headphones with noise cancellation",
          image: "🎧"
        },
        { 
          id: 2, 
          name: "Smart Watch", 
          price: 199.99, 
          description: "Feature-rich smartwatch with health monitoring",
          image: "⌚"
        },
        { 
          id: 3, 
          name: "Laptop Backpack", 
          price: 49.99, 
          description: "Durable laptop backpack with USB charging port",
          image: "🎒"
        },
        { 
          id: 4, 
          name: "Bluetooth Speaker", 
          price: 79.99, 
          description: "Portable Bluetooth speaker with 360° sound",
          image: "🔊"
        },
        { 
          id: 5, 
          name: "Phone Case", 
          price: 24.99, 
          description: "Protective phone case with sleek design",
          image: "📱"
        },
        { 
          id: 6, 
          name: "Gaming Mouse", 
          price: 59.99, 
          description: "Precision gaming mouse with RGB lighting",
          image: "🖱️"
        },
        { 
          id: 7, 
          name: "Mechanical Keyboard", 
          price: 89.99, 
          description: "Tactile mechanical keyboard for typing enthusiasts",
          image: "⌨️"
        },
        { 
          id: 8, 
          name: "USB-C Hub", 
          price: 39.99, 
          description: "Multi-port USB-C hub for all your devices",
          image: "🔌"
        }
      ];
      setProducts(mockProducts);
    } finally {
      setLoading(false);
    }
  };

  // Fetch cart from backend on page load and sync
  const fetchCartFromBackend = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/cart');
      if (response.ok) {
        const data = await response.json();
        setCart(data.items); // Sync frontend with backend cart
        console.log('🔄 Cart synced with backend:', data.items);
      }
    } catch (error) {
      console.error('Error fetching cart from backend:', error);
      // If backend fails, localStorage will handle it
    }
  };

  const addToCart = async (product) => {
    try {
      const response = await fetch('http://localhost:5000/api/cart', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          productId: product.id,
          quantity: 1
        })
      });

      if (!response.ok) {
        throw new Error('Failed to add item to cart');
      }

      // After successful backend operation, sync with backend to get latest cart
      await fetchCartFromBackend();

    } catch (error) {
      console.error('Error adding to cart:', error);
      // Fallback to local state if API fails
      const existingItem = cart.find(item => item.id === product.id);
      if (existingItem) {
        setCart(cart.map(item =>
          item.id === product.id 
            ? { ...item, quantity: item.quantity + 1 }
            : item
        ));
      } else {
        setCart([...cart, { 
          id: product.id,
          productId: product.id,
          name: product.name, 
          price: product.price, 
          quantity: 1 
        }]);
      }
    }
  };

  // Function to manually refresh cart (useful for debugging)
  const refreshCart = async () => {
    console.log('🔄 Manually refreshing cart...');
    await fetchCartFromBackend();
  };

  if (loading) {
    return (
      <div className="products-section">
        <h2>Products</h2>
        <div className="loading">Loading products...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="products-section">
        <h2>Products</h2>
        <div className="error-message">
          <p>{error}</p>
          <div style={{ marginTop: '10px' }}>
            <button onClick={fetchProducts} className="retry-btn">
              Retry Products
            </button>
            <button onClick={refreshCart} className="retry-btn" style={{ marginLeft: '10px' }}>
              Refresh Cart
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="products-section">
      <h2>Products</h2>

      {/* Cart Status Display */}
      <div style={{ 
        marginBottom: '20px', 
        padding: '10px', 
        background: '#f8f9fa', 
        borderRadius: '5px',
        border: '1px solid #e9ecef'
      }}>
        <strong>Cart Status:</strong> {cart.length} items in cart
        <button 
          onClick={refreshCart}
          style={{ 
            marginLeft: '10px', 
            padding: '5px 10px', 
            background: '#007bff', 
            color: 'white', 
            border: 'none', 
            borderRadius: '3px',
            cursor: 'pointer'
          }}
        >
          Refresh Cart
        </button>
      </div>

      <div className="product-grid">
        {products.map(product => (
          <ProductCard 
            key={product.id} 
            product={product} 
            onAddToCart={addToCart}
          />
        ))}
      </div>
    </div>
  );
};

export default ProductGrid;
