import React, { useState, useEffect } from 'react';
import ProductCard from './ProductCard';

const ProductGrid = ({ cart, setCart }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await fetch('http://localhost:5000/api/products');

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error('Error fetching products:', error);
      setError('Failed to load products. Please try again later.');

      // Fallback to mock data if API is not available
      const mockProducts = [
        { 
          id: 1, 
          name: "Wireless Headphones", 
          price: 99.99, 
          description: "High-quality wireless headphones with noise cancellation",
          image: "ðŸŽ§"
        },
        { 
          id: 2, 
          name: "Smart Watch", 
          price: 199.99, 
          description: "Feature-rich smartwatch with health monitoring",
          image: "âŒš"
        },
        { 
          id: 3, 
          name: "Laptop Backpack", 
          price: 49.99, 
          description: "Durable laptop backpack with USB charging port",
          image: "ðŸŽ’"
        },
        { 
          id: 4, 
          name: "Bluetooth Speaker", 
          price: 79.99, 
          description: "Portable Bluetooth speaker with 360Â° sound",
          image: "ðŸ”Š"
        },
        { 
          id: 5, 
          name: "Phone Case", 
          price: 24.99, 
          description: "Protective phone case with sleek design",
          image: "ðŸ“±"
        }
      ];
      setProducts(mockProducts);
    } finally {
      setLoading(false);
    }
  };

  const addToCart = async (product) => {
    try {
      const response = await fetch('http://localhost:5000/api/cart', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          productId: product.id,
          quantity: 1
        })
      });

      if (!response.ok) {
        throw new Error('Failed to add item to cart');
      }

      const result = await response.json();

      // Update local cart state based on backend response
      // This ensures frontend and backend are in sync
      const existingItem = cart.find(item => item.productId === product.id);
      if (existingItem) {
        setCart(cart.map(item =>
          item.productId === product.id 
            ? { ...item, quantity: item.quantity + 1 }
            : item
        ));
      } else {
        setCart([...cart, { 
          id: product.id,
          productId: product.id,
          name: product.name, 
          price: product.price, 
          quantity: 1 
        }]);
      }

    } catch (error) {
      console.error('Error adding to cart:', error);
      // Fallback to local state if API fails
      const existingItem = cart.find(item => item.id === product.id);
      if (existingItem) {
        setCart(cart.map(item =>
          item.id === product.id 
            ? { ...item, quantity: item.quantity + 1 }
            : item
        ));
      } else {
        setCart([...cart, { ...product, quantity: 1 }]);
      }
    }
  };

  if (loading) {
    return (
      <div className="products-section">
        <h2>Products</h2>
        <div className="loading">Loading products...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="products-section">
        <h2>Products</h2>
        <div className="error-message">
          <p>{error}</p>
          <button onClick={fetchProducts} className="retry-btn">
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="products-section">
      <h2>Products</h2>
      <div className="product-grid">
        {products.map(product => (
          <ProductCard 
            key={product.id} 
            product={product} 
            onAddToCart={addToCart}
          />
        ))}
      </div>
    </div>
  );
};

export default ProductGrid;
